function getAllCactusData(cas, name) {
  cas = typeof cas === 'undefined' ? $('#cas').val() : cas;
  name = typeof name === 'undefined' ? $('#name').val() : name;
  var delay = 1;

  if (cas === '') {
    if (name === '') {
      new Notify('notice', 'Fill at least CAS or name of the chemical (both to increase the chance of getting requested data)');
      return;
    } else {
      delay = 1000;
      getCactusData('cas');
    }
  }

  setTimeout(function () {
    getCactusData('iupac_name');
    getCactusData('chemspider_id');
    getCactusData('mw');
    getCactusData('formula');
    getCactusData('sdf');
    getCactusData('smiles');
    getCactusData('stdinchikey');
    getCactusData('stdinchi');
  }, delay);
}

function getCactusData(type) {
  var cas = $('#cas').val().split(';')[0],
      name = $('#name').val().replace('(+)-', '').replace('()-', '').replace('()-', ''),
      skipCas = false;

  if (cas === '') {
    if (name === '') {
      new Notify('notice', 'Fill at least CAS or name of the chemical (both to increase the chance of getting requested data)');
      return;
    } else skipCas = true;
  }

  var url = 'https://cactus.nci.nih.gov/chemical/structure';
  if (type === 'sdf') url += '?operator=remove_hydrogens'; //$('#chemical-data-icon').addClass('fa-spin');

  $('#spinner').show();
  $.get(url, {
    string: skipCas ? name : cas,
    representation: type
  }).done(function (data) {
    fillCactusData(type, data);
    $('#spinner').hide(); //$('#chemical-data-icon').removeClass('fa-spin');
  }).fail(function () {
    if (skipCas) return;
    $.get(url, {
      string: name,
      representation: type
    }).done(function (data) {
      fillCactusData(type, data);
    }).always(function () {
      //$('#chemical-data-icon').removeClass('fa-spin');
      $('#spinner').hide();
    });
  });
}

function fillCactusData(type, data) {
  // check to prevent inserting invalid data
  if (data.indexOf('<!DOCTYPE') !== -1) return;

  switch (type) {
    case 'mw':
    case 'formula':
    case 'smiles':
      {
        $('#' + type).val(data);
        break;
      }

    case 'iupac_name':
    case 'cas':
      {
        $('#' + type).val(data.split('\n').join(';'));
        break;
      }

    case 'chemspider_id':
      {
        $('#' + type.replace('_id', '')).val(data.split('\n').join(';'));
        break;
      }

    case 'sdf':
      {
        $('#' + type).val(data);
        $('iframe#ketcher').renderStructure('molecule', data);
        break;
      }

    case 'stdinchikey':
    case 'stdinchi':
      {
        var strip = type === 'stdinchikey' ? 'InChIKey=' : 'InChI=';
        $('#' + type.replace('std', '')).val(data.replace(strip, ''));
        break;
      }
  }
}

